function App() {


  const [inputText, setInputText] = useState('');
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState('all');
  const [filteredTodos, setFilteredTodos] = useState([]);

  useEffect(() => {
    filterHandler();
  }, [todos]);

  const filterHandler = () => {
    switch(status){
      case 'completed':
        setFilteredTodos(todos.filter(todo => todo.completed === true));
        break;
       case 'uncompleted':
        setFilteredTodos(todos.filter(todo => todo.completed === false));
        break;
       default:
         setFilteredTodos(todos);
         break;
      }
  }
  return (
    <div className="App">
      <header>
        <h1>Cose da Fare</h1>
      </header>
        <Form 
        inputText={inputText}
        todos={todos}
        setTodos={setTodos}
        setInputText={setInputText} 
        setStatus={setStatus}
        />
        <TodoList 
        setTodos={setTodos}
        todos={todos} />
    </div>
  );
}


export default App;
