import React, { useState, useEffect } from 'react';
import './App.css';
import Form from './components/Form'
import TodoList from './components/TodoList';


function App() {

  const [inputText, setInputText] = useState('');
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState('all');
  const [filteredTodos, setFilteredTodos] = useState([]);

  useEffect(() => {
    getLocalTodos();
  }, []);

  useEffect(() => {
    filterHandler();
    saveLocalTodos();
  }, [todos, status]);

  const filterHandler = () => {
    switch(status){
      case "completed":
        setFilteredTodos(todos.filter(todo => todo.completed === true));
        break;
       case "uncompleted":
        setFilteredTodos(todos.filter(todo => todo.completed === false));
        break;
       default:
         setFilteredTodos(todos);
         break;
      }
  };

  const saveLocalTodos = () => {
      localStorage.setItem("todos", JSON.stringify(todos));
  };

  const getLocalTodos = () => {
    if(localStorage.getItem("todos") ===null){
      localStorage.setItem("todos", JSON.stringify([]));
    }else{
    let todolocal = JSON.parse(localStorage.getItem("todos"));
    setTodos(todolocal);
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Cose da Fare</h1>
      </header>
        <Form 
        inputText={inputText}
        todos={todos}
        setTodos={setTodos}
        setInputText={setInputText} 
        setStatus={setStatus}
        />
        <TodoList 
        filteredTodos={filteredTodos}
        setTodos={setTodos}
        todos={todos} 
        />
      <div className="logo">
    <img
          src="https://www.google.com/url?sa=i&url=https%3A%2F%2F1000logos.net%2Finternazionale-logo%2F&psig=AOvVaw0MXcjOu4iicgs0qHor5koR&ust=1603360043955000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCNjw3-CzxewCFQAAAAAdAAAAABAD"
          alt="logo"
        />
    </div>

    </div>
  );
}


export default App;
